class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();

        int cnt=0,i=0,ones=0;

        while(i<n-1){
            if(s[i]=='1' && s[i+1]=='0'){
                cnt+=( ones + 1);
            }
            if(s[i]=='1') ones++;
            i++;
        }
        return cnt;
    }
};
