class Solution {
public:
    int maxOperations(string s) {
        vector<int>temp;
        int cnt = 0;
        int k = 0;
        bool flag = false;

        for(char ch:s){
            if(ch == '1'){
                temp.push_back(1);
                flag = true;
                }

            else if(flag){
                temp.push_back(0);
                flag = false;
            }
        }
        for(int i : temp){
            if(i == 1) k ++;
            else cnt += k;
        }
        return cnt;
    }
};
