class Solution:
    def maxOperations(self, s: str) -> int:
        num_1s = 0
        ans = 0

        prev = '0'
        for c in s:
            if c == '0' and prev == '0':
                continue
            if c == '0' and prev == '1':
                ans += num_1s
            if c == '1':
                num_1s += 1

            prev = c

        return ans
