class Solution {
public:
    int maxOperations(string s) {
        int c=0,ans=0;
        int n=s.length();
        c=(s[n-1]=='0');
        for(int i=n-2;i>=0;i--){
            if(s[i]=='0'&&s[i+1]=='1')
                c++;
            else if(s[i]=='1')
              { 
                ans+=c;

            }
        }
        return ans;
    }
};
