class Solution {
public:
    int maxOperations(string s) {
        int n = (int)s.size();
        vector<int> cnt;
        int cur = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                cur++;
            }
            if (s[i] == '0') {
                if (cur) cnt.push_back(cur);
                cur = 0;
            }
        }
        if (cur) cnt.push_back(cur);
        int m = (int)cnt.size();
        int answer = 0;
        for (int i = 0; i < m - 1; i++) {
            answer += cnt[i];
            cnt[i + 1] += cnt[i];
        }
        if (s[n - 1] == '0' && !cnt.empty()) answer += cnt.back();
        return answer;
    }
};
