#include<bits/stdc++.h>
using namespace std;
class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        bool p = false;
        s += '1';
        for(int i = 0; i < s.size(); i ++) {
            if(s[i] == '1') {
                if(p) {
                    ans += cnt;
                }
                cnt += 1;
                p = false;
            } else {
                p = true;
            }
        }
        return ans;
    }
};
