class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int c0 = 0, ret = 0;
        for (int i = n - 1; i >= 0; --i) {
            if (s[i] == '0') {
                if (i == n - 1 || s[i + 1] != '0') {
                    c0++;
                }
            } else {
                ret += c0;
            }
        }
        return ret;
    }
};
