class Solution {
    public int maxOperations(String s) {
         int ans = 0;
        int ct0 = 0;
        boolean poss = false;
        ArrayList<Integer>possible = new ArrayList<>();
       finalarr(possible,s,poss);
       for (int value : possible) {
            if (value == 1) {
                ct0++;
            } else {
                ans += ct0;
            }
        }

        return ans;
    }
    static void finalarr(ArrayList<Integer>possible,String s, boolean poss){
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                possible.add(1);
                poss = true;
            }
            if (s.charAt(i) == '0' && poss) {
                possible.add(0);
                poss = false;
            }
        }
    }
}
