class Solution {
public:
    int maxOperations(string s) {
        int one=0;

        int n=s.size();
        int ans=0;
        if(n==1) return 0;
        for(int i=0;i<n;i++){
            if(s[i]=='1') one++;
            else{
                ans+=one;
                while(i<n&&s[i]=='0'){
                    i++;
                }
                one++;
            }

        }
        return ans;
    }
};
