class Solution:
    def maxOperations(self, s: str) -> int:
        tmp = ""
        last = ""
        for c in s:
            if c == last == "0":
                continue
            else:
                tmp += c
            last = c
        s = tmp
        n = len(s)
        ans = 0
        i, j = 0, n - 1
        while i < j:
            if s[i] == '1':
                while j > i and s[j] == '1':
                    j -= 1

                if j > i:
                    ans += j - i
                j -= 1
            i += 1
        return ans
