class Solution {
public:
    int maxOperations(string s) {
        char last='a';
        int res=0,count=0;
        for(auto c: s){
            if(c=='1'){
                count++;
            }
            else if(c=='0' && last=='1'){
                res+=count;
            }
            last=c;
        }
        return res;
    }
};
