class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.length();
        vector<int> pre(n,0);
        pre[0] = (s[0] == '1')?1:0;
        for(int i=1; i<n; i++){
            pre[i] = pre[i-1];
            if(s[i] == '1') pre[i]++;
        }
        // for(auto a: pre) cout<<a<<" ";
        // cout<<endl;
        for(int j = n-1; j>0; j--){
            if(s[j] == '0' && s[j-1] == '1'){
                // cout<<j<<" "<<s[j]<<" "<<s[j-1]<<endl;
                ans+= pre[j];
            }
        }
        return ans;
    }
};
