class Solution {
public:
    int maxOperations(string s) {
        int cnt_of_1=0;
        int ans=0;
        for(int i=0;i<s.length()-1;i++){
            if(s[i]=='1') {
                cnt_of_1++;

                int j=i+1;
                for(;j<s.length();j++){
                    if(s[j]=='1') break;
                }
                j--;
                if(s[j]=='0'){
                    ans+=cnt_of_1;
                }
            }

        }

        return ans;
    }
};
