class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),i=n-1,cnt=0,sum=0;
        for(int i=0;i<n-1;i++) {
            if(s[i]=='0' && s[i+1]=='1') sum+=cnt;
            cnt+=(s[i]=='1');
        }
        if(s[n-1]=='0') sum+=cnt;
        return sum;
    }
};
