class Solution {
public:
    int maxOperations(string binaryString){
        int result = 0;
        int countOnes = 0;
        bool encounteredOne = false;
        vector<int> bitsSequence;

        for(auto character : binaryString){
            if(character == '1'){
                bitsSequence.push_back(1);
                encounteredOne = true;
            }
            else if(character == '0' && encounteredOne == true){
                bitsSequence.push_back(0);
                encounteredOne = false;
            }
        }

        for(auto bit : bitsSequence){
            if(bit == 1){
                countOnes++;
            }
            else{
                result += countOnes;
            }
        }

        return result;
    }

};
