class Solution { 
    public int maxOperations(String s) { 
        int result = 0;
        int count_1 = 0; 
        boolean found_one = false; 
        List<Integer> sequence = new ArrayList<>(); 

        for (int i = 0; i < s.length(); i++) { 
            if (s.charAt(i) == '1') { 
                sequence.add(1); 
                found_one = true; 
            } 
            if (s.charAt(i) == '0' && found_one) { 
                sequence.add(0); 
                found_one = false; 
            } 
        } 

        for (int num : sequence) { 
            if (num == 1) { 
                count_1++; 
            } else { 
                result += count_1; 
            } 
        } 

        return result; 
    } 
}
