class Solution {
public:
    int maxOperations(string s) {
        int sta = 1;
        vector<int> v;
        int i = 0;
        while (i < s.size() && s[i] == '0')i++;
        for (; i < s.size();) {
            int cnt = 1;
            while (i + 1 < s.size() && s[i + 1] == s[i]) {
                cnt++;
                i++;
            }
            v.push_back(cnt);
            i++;
        }
        // for(auto &ii : v) cout << ii << " ";
        // cout <<endl;
        int n = v.size();
        if (s[s.size() - 1] == '1')v.resize(n - 1);
        n = v.size();
        int ans = 0;

        for (int i = 0; i < v.size(); ++i) {
            if(i % 2 == 0)
                ans += v[i] * (n-i)/2;

        }
        return ans;
    }
};
