class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        int cnt = 0;
        for(int i = 0; i < n; i++){
            if(s[i] == '0' && (i + 1 < n && s[i + 1] == '1')){
                ans += cnt;
            }
            if(s[i] == '1'){
                cnt++;
            }
        }
        if(s[n-1] == '0') ans +=cnt;
        return ans;
    }
};
