class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        bool add = false;

        for (char x: s) {
            if (x == '0') {
                if (!add)
                    ans += cnt;
                add = true;
            } else {
                cnt++;
                add = false;
            }
        }

        return ans;
    }
};
