class Solution {
public:
    int maxOperations(string s) {
       // stack<int> s;
        int count=0,n1=0,i=0,n=s.size();
         while(i<n){
             int x=s.at(i)-'0';
             int n0=0;
             while(i<n && s.at(i)=='1'){
                 n1++;
                 i++;
             }
              while(i<n && s.at(i)=='0'){
                 n0++;
                 i++;
             }
             if(n0>0) count+=n1;
         }
        return count;
    }
};
