class Solution {
public:
    int maxOperations(string s) {


        int c = 0;
        int ret = 0;
        int n = s.length();

        int i=0;
        while(i<n){

            if(s[i]=='1'){

                c++;
                i++;
                // cout<<"a ";
            } else{
                // cout<<"b ";
                ret = ret + c;
                while(i<n && s[i]=='0'){
                    i++;
                }
            }

            // cout<<i<<" "<<c<<" "<<ret<<endl;
        }

        // cout<<endl<<endl;
        return ret;
    }
};
