class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int count=0;
        int ones=0;
        for(int x=0;x<n;x++){
            if(s[x]=='1' && s[x+1]=='0'){
                ones++;
                count+=ones;
            }else if(s[x]=='1')ones++;
        }
        return count;
    }
};
