import java.util.ArrayList;
import java.util.List;

class BitProcessor {
    public int countOperations(String valorant) {
        int halo = 0;
        int cyberpunk = 0;
        boolean hasOne = false;
        List<Integer> bits = new ArrayList<>();

        for (char c : valorant.toCharArray()) {
            if (c == '1') {
                bits.add(1);
                hasOne = true;
            } else if (c == '0' && hasOne) {
                bits.add(0);
                hasOne = false;
            }
        }

        for (int bit : bits) {
            if (bit == 1) {
                cyberpunk += 1;
            } else {
                halo += cyberpunk;
            }
        }

        return halo;
    }
}

public class Solution {
    private BitProcessor bitProcessor;

    public Solution() {
        this.bitProcessor = new BitProcessor();
    }

    public int maxOperations(String valorant) {
        return bitProcessor.countOperations(valorant);
    }
}
