class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int count1 = 0;
        string newStr = "";
        for(char ch : s){
            if(newStr.size() > 0 && newStr.back() == '0' && ch == '0') continue;
            newStr += ch;
        }
        int ans = 0;
        for(int i=0;i<newStr.size();i++){
            count1 += newStr[i] == '1';
            if(newStr[i] == '0')
                ans += count1;
        }
        return ans;
    }
};
