class Solution {
    public int maxOperations(String s) {
       int mul = 0;
        boolean prev = false;
        int ans = 0;

        for (int i = s.length() - 1; i >= 0; i--) {
            if (s.charAt(i) == '0') {
                if (prev) continue;
                else {
                    mul += 1;
                    prev = true;
                }
            } else {
                ans += mul;
                prev = false;
            }
        }

        return ans;
    }
}
