class Solution {
public:
    int maxOperations(string s) {
        int one = 0;
        bool firstzero = 0;
        int ans = 0;
        for(int i=0; i<s.size(); i++){
            if(s[i]=='1'){
                one++;
                firstzero=1;
            }
            if(s[i]=='0' && firstzero){
                ans+=one;
                firstzero=0;
            }
        }
        return ans;

    }
};
