class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int c1 = 0, c0 = 0;
        int res = 0;
        for(int i=0;i<n;i++){
            if(s[i] == '1'){
                if(c0 > 0) res += (c1);
                c1++;
                c0 = 0;
            } else {
                c0++;
            }
        }
        if(c0 > 0){
            res += c1;
        }
        return res;
    }
};
