class Solution {
    public int maxOperations(String s) {
        List<Integer> list = new ArrayList<>();
        int n = s.length();
        for(int i = 0; i < n; i++){
            if(s.charAt(i) == '1'){
                list.add(i);
            }
        }
        int ans = 0;
        int count = 1;
        for(int i = 1; i < list.size(); i++){
            if(list.get(i) != list.get(i - 1) + 1){
                ans += count;
            }
            count++;
        }
        if(list.size() > 0 && s.charAt(n - 1) == '0'){
            ans += count;
        }
        return ans;
    }
}
