class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, count = 0, start = 0;
        while(start < s.length()) {
            while(start < s.length() && s[start] == '1')
                count++, start++;

            if(start < s.length() && s[start] == '0')
                ans += count;

            while(start < s.length() && s[start] == '0')
                    start++;
        }

        return ans;
    }
};
