class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), cnt = 0, prev = 0, r = 0, w = 0;
        for (char c = '0'; r < n; r++) {
            if (s[r] == '1') {
                s[w++] = '1';
                c = '1';
            } else if (c == '1') {
                s[w++] = '0';
                c = '0';
            }
        }
        for (int i = 0; i < w; i++) {
            if (s[i] == '1') { prev++; }
            else {
                cnt += prev;
            }
        }
        return cnt;
    }
};
