class Solution {
public:
    int maxOperations(string s) {
int max_op=0;
int val=0;
bool flag=true;
for(int i=0;i<s.length();i++){
if(s[i]=='1'){
     val++;
     flag=true;
         }
else{
     if(flag) max_op += val;
     flag=false;
    }
}
return max_op;
 // Code by: kanhaiya mittal
 }
};
