class Solution:
    def maxOperations(self, s: str) -> int:
        x = len(s) - 1
        while x >= 0 and s[x] == '1':
            x -= 1
        block, cnt = [], 0
        for y in range(x + 1):
            if s[y ]== '1':
                cnt += 1
            elif cnt > 0:
                block.append(cnt)
                cnt = 0
        # print(x)
        # print(block)
        res, s = 0, 0
        for i in range(len(block)):
            s += block[i]
            res += s # block * (len(block) - i)
        return res
