class Solution:
    def maxOperations(self, s: str) -> int:
        total = 0
        groups = 0
        start_counting = False
        i = len(s) - 1
        while i >= 0 and s[i] == "1":
            i -= 1
        while i >= 0:
            if s[i] == "1":
                ones = 0
                while i >= 0 and s[i] == "1":
                    ones += 1
                    i -= 1
                groups += 1
                total += ones*groups
            i -= 1
        return total
