class Solution:
    def maxOperations(self, s: str) -> int:
        def lint(s):
            ans = []
            prev = '1'
            for c in s:
                if c == '1':
                    ans.append(c)
                    prev = '1'
                else:
                    if c != prev:
                        ans.append(c)
                        prev = c
            return ''.join(ans)
        s = lint(s)
        ans = 0
        eff = 0
        for c in s[::-1]:
            if c == '1':
                ans += eff
            else:
                eff += 1
        return ans
