class Solution {
public:
    int maxOperations(string s) {
        int i = s.size() - 1, res = 0, cnt = 1, flag = 1;
        if(s[i] == '1') cnt = 0;

        while(i >= 0 && s[i] != '1') i--;

        for( ; i >= 0; i--) {
            if(s[i] == '0') {
                if(flag) cnt++;
                flag = 0;
            }
            else {
                res = res + cnt;
                flag = 1;
            }
        }

        return res;
    }
};
