class Solution {
public:
    int maxOperations(string s) {
        int cnt1 = 0, ans = 0;
        bool canAdd = true;
        for(char ch: s) {
            if(ch == '0') {
                if(canAdd) {
                    ans += cnt1;
                    canAdd = false;
                }
            } else {
                ++cnt1;
                canAdd = true;
            }
        }
        return ans;
    }
};
