class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> v(n,0);
        int curcnt=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='1'){
                v[i] = curcnt;
            }
            else{
                if(s[i]=='0' && (i==n-1 || s[i+1]=='1')){
                    curcnt++;
                }
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            ans+=v[i];
        }
        return ans;
    }
};
