using namespace std;

class Solution {
public:
    int maxOperations(string s) {
        ll cnt = 0;
        ll n = s.size();

        ll last = 0;
        for (int i = n-1; i >= 0; i--) {
            if (s[i] == '1') {
                ll aux = last;
                aux += (i < n-1 && s[i+1] == '0');
                cnt += aux;
                last = aux;
            }
        }
        return cnt;
    }
};

