class Solution {
    public int maxOperations(String s) {

        int cnt = 0;

        int res = 0;

        boolean f = false;
        for(int i = 0; i < s.length(); i++){
            char c = s.charAt(i);
            if(c =='1'){
                cnt++;
                f =false;
            }else if(c == '0'){
                if(!f){
                    res += cnt;
                    f = true;
                }

            }
        }
        return res;
    }
}
