class Solution {
    public int maxOperations(String s) {
        int c = s.charAt(0) - '0', ans = 0;
        for(int i = 1; i < s.length(); ++i) {
            if(s.charAt(i) == '0' && s.charAt(i - 1) == '1') {
                ans += c;
            } else if(s.charAt(i) == '1') {
                c++;
            }
        }
        return ans;
    }
}
