class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int one = 0;
        bool flage = false;
        for(auto it:s){
            if(it == '0'){
                if(flage)ans+=one;
                flage = false;
            }
            else{
                flage = true;
                one++;
            }
        }
        return ans;
    }
};
