class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int ones = 0;
        int i = 0;
        while (i < s.length()) {
            boolean hasZero = false;
            while (i < s.length() && s.charAt(i) == '0') {
                hasZero = true;
                i++;
            }
            if (hasZero) ans += ones;
            if (i < s.length()) ones++;
            i++;
        }
        return ans;
    }
}
