class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, n = s.length();
        vector<int> v(n);
        for(int i = 1; i < s.length(); ++i)
        {
            v[i] = v[i - 1] + (s[i - 1] == '1' && s[i] == '0');
        }
        for(int i = 0; i < s.length(); ++i)
        {
            if(s[i] == '1')
                ans += v[n - 1] - v[i];
        }
        return ans;
    }
};
