class Solution {
public:
    int maxOperations(string s) {
      int n = s.size();
      int mx =0;
      bool group = false;
      int cur =0;

    //  queue<int>q1,q2;

      vector<int> arr ;
      for(int i=0;i<n;++i){
        if(s[i]=='1'){
          group = true;
          cur++;
        }
        else{
          if(group){
            group = false;
            arr.push_back(cur);
            cur=0;
          }
        }
      }
/*
      while(!q1.empty()){
        while(!q1.empty()){
          int cur = q1.front();
          q1.pop();


        }

      }*/
      int prev =0;
      for(int i=0;i<arr.size();++i){
        mx += prev+ arr[i];
        prev+=arr[i];
      }

      return mx;  
    }
};
