class Solution {
public:
    int maxOperations(string s) {
        s+='1';
        int ans=0, n=s.size();
        int cnt=0;

        for (int i=0;i<n-1;i++){
            if (s[i]=='1'){
                cnt++;
            }
            else{
                ans+=cnt;
                while (s[i+1]!='1') i++; 

            }
        }
        return ans;
    }
};
