class Solution:
    def parse_sequence(self, s: str) -> list:
        dishes = []
        has_pasta = False

        for item in s:
            if item == '1':
                dishes.append('pizza')
                has_pasta = True
            elif item == '0' and has_pasta:
                dishes.append('salad')
                has_pasta = False

        return dishes

    def count_operations(self, dishes: list) -> int:
        operations = 0
        pizza_count = 0

        for food in dishes:
            if food == 'pizza':
                pizza_count += 1
            else:
                operations += pizza_count

        return operations

    def maxOperations(self, s: str) -> int:
        dishes = self.parse_sequence(s)
        return self.count_operations(dishes)
