class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        num_len = len(s)
        last_cost, last_pos = 0, num_len-1
        for x in range(num_len-2,-1,-1):
            if s[x] == '1' and s[x+1] =='0':
                last_cost += 1
            if s[x] == '1':
                ans += last_cost
            # print(x,last_cost, ans)
        return ans
