class Solution:
    def maxOperations(self, s: str) -> int:
        ones = []
        i = 0
        n = len(s)

        while i < n:
            while i < n and s[i] == '0': i += 1
            oc = 0
            while i < n and s[i] == '1':
                i += 1
                oc += 1
            if oc != 0: ones.append(oc)

        su = 0
        ans = 0
        for i in ones:
            su += i
            ans += su

        if s[-1] == '1': ans -= sum(ones)

        return ans
