class Solution:
    def maxOperations(self, s: str) -> int:
        s = s + "0"
        cnt = 0
        chunk = []
        for c in s:
            if c == "1":
                cnt += 1
            elif cnt > 0:
                chunk.append(cnt)
                cnt = 0
        if s[-2] == "1":
            chunk.pop()

        ans = 0
        cul = 0
        for val in chunk:
            cul += val
            ans += cul

        return ans
