class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int p = 0;
        bool f = false;
        vector<int> b;

        for (auto c : s) {
            if (c == '1') {
                b.push_back(1);
                f = true;
            } else if (c == '0' && f) {
                b.push_back(0);
                f = false;
            }
        }

        for (auto i : b) {
            if (i == 1) {
                p += 1;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
